<?php$MSG = '';$lib = new Linia();if(!empty($_POST['dodaj_linia'])  ){	if ($lib->addLinia($_POST['nazwa_linii'])) {		$message = 'Linia została pomyślnie dodana';		$MSG = Msg($message,1);	} else {		$message = 'Błąd dodawania linii. Sprawdź czy linia jest unikalna';		$MSG = Msg($message,0);			}   }if(!empty($_POST['zapisz_linia']) ){	if ($lib->updateLinia($_POST['id_linii'] , $_POST['nazwa_linii'])) {		$message = 'Linia została pomyślnie zaktualizowana';		$MSG = Msg($message,1);	} else {		$message = 'Błąd aktualizacji linii';		$MSG = Msg($message,0);			}}if(!empty($_POST['usun_linia'])){			if ($lib->deleteLinia($_POST['id_linii'])) {			$message = 'Linia została usunięta';		$MSG = Msg($message,1);	} else {		$message = 'Błąd usuwania linii';		$MSG = Msg($message,0);			}		/* $lib->StartTransaction();	if($lib->deleteLinia($_POST['id_linii']))	{						$lib2 = & new Odjazdy();		if ($lib2->deleteOdjazdy($_POST['id_linii'])) 		$lib->Commit();	}else	$lib->Rollback();	    }    */}?>