<?php$lib = new Trasa();$MSG = '';if(!empty($_POST['dodaj_trasa'])  ){	$trasy = implode(';',$_POST['trasa']);	if ($lib->addTrasa($_POST['kierunek'],$trasy)) {		$message = 'Trasa została pomyślnie dodana';		$MSG = Msg($message,1);	} else {		$message = 'Błąd dodawania trasy';		$MSG = Msg($message,0);			}   } if(!empty($_POST['usun_trasa'])){			if ($lib->deleteTrasa($_POST['id_trasa'])) {			$message = 'Trasa została pomyślnie usunięta';		$MSG = Msg($message,1);	} else {		$message = 'Błąd usuwania trasy';		$MSG = Msg($message,0);			}				/* $lib->StartTransaction();	if($lib->deleteLinia($_POST['id_linii']))	{						$lib2 = & new Odjazdy();		if ($lib2->deleteOdjazdy($_POST['id_linii'])) 		$lib->Commit();	}else	$lib->Rollback();	    }*/     }if (! empty($_POST['save_trasa']) && ! empty($_POST['trasa'])){    $str = implode(';', $_POST['trasa']);    if ($lib->updateTrasa($_POST['id_trasa'], $_POST['kierunek'], $str)) {    	$message = 'Trasa została pomyślnie zaktualizowana';    	$MSG = Msg($message, 1);	} else {    	$message = 'Błąd aktualizacji trasy.';    	$MSG = Msg($message, 0);			}}if (!empty($_POST['id_trasa'])){    $id_trasa = $_POST['id_trasa'];    $trasa_more = $lib->getDetails('trasa', 'id_trasa', $id_trasa);}?>